<svelte:head>	<title>Study</title>	<meta name="description" content="Study this app" /></svelte:head><script>	import { onMount } from "svelte";		// 처음에 화면을 띄우자마자 실행	onMount( async () => {		inputFormTag.focus();	})		let todos = [];	let inputFormTag;	let todo = '';	$: console.log( todo );	const addTodo = () => {		if(!todo.trim() ) {			alert('할 일을 추가하주세요!');		} else {			todos.push(todo);			todos = todos;		}		todo = '';		inputFormTag.focus();	}	const deleteTodoAll = () => {		if(todos.length === 0) {			alert('삭제할 todo가 없습니다.');			inputFormTag.focus();			return;		}		if(confirm('주의하세요! 모두 삭제됩니다.')) {			todos = [];		}		inputFormTag.focus();	}</script><div class="text-column">	<h1>Svelte Todo List</h1>	<input type='text'   bind:this={inputFormTag}   bind:value={todo}   on:keydown={() => {		 event.key === 'Enter' && addTodo();	 }}/>	<button on:click={addTodo}>할 일 추가하기</button>	<ul>		{ #each todos as item }		<li>{ item }</li>		{ :else }		<li>추가된 할 일이 없습니다.</li>		{ /each }	</ul>	<hr />	<button on:click={ deleteTodoAll }>할 일 전체삭제</button></div>